function populateRecent(items,ele){
    let category_carrirer = ``
   items.forEach( (category,i)=>{
let price = parseInt(category.itdct) ==1 ?parseFloat( category.itsp ):   (parseFloat( category.itsp) -   parseFloat( category.itsp)*parseFloat(category.itdct)) 

 
  category_carrirer += `<div class="item-pro-color">
                                  <div class="product-thumb">
                                      <a href="/item_details/product/${category.itid}" class="product-thumb-link">
                             <img alt="" class="active"  src="/${JSON.parse(category.itimg).img[0]}"/>
                                          
                                      </a>
                                      <a href="/item_details/product/${category.itid}" class="quickview-lin plus fancybox.ifram"><span>quick view</span></a>
                                  </div>
                                  <div class="product-info">
                                      
                                  <h3 class="product-title"><a href="/item_details/product/${category.itid}">${category.itn}</a></h3>
                                      <div class="product-price">
                                          <ins><span>₦${new Intl.NumberFormat('en').format(parseFloat(price).toFixed(1) ) }</span></ins>
                                          <del><span>${category.itsp}</span></del>
                                      </div>
                                  </div>
                              </div>`

})
ele.innerHTML = category_carrirer;
document.querySelector(".rev_").setAttribute("style",`margin-bottom: 30px;display: block;`)
function owl_controller(){
    function background(){
	$('.bg-slider .item-banner').each(function(){
		var src=$(this).find('.banner-thumb a img').attr('src');
		$(this).css('background-image','url("'+src+'")');
	});	
}

function animated(){
    $('.banner-slider .owl-item').each(function(){
      var check = $(this).hasClass('active');
      if(check==true){
        $(this).find('.animated').each(function(){
          var anime = $(this).attr('data-animated');
          $(this).addClass(anime);
        });
      }else{
        $(this).find('.animated').each(function(){
          var anime = $(this).attr('data-animated');
          $(this).removeClass(anime);
        });
      }
    });
  }
  /***************************************************************************************************************/ 
  
    //Owl Carousel
    ////////////////////////////////////////////////
    let num_count = 0;
     let intDeal = setInterval(()=>{
       num_count++;
    if($('.wrap-item').length>0){
      $('.wrap-item').each(function(){
        var data = $(this).data();
        $(this).owlCarousel({
          addClassActive:true,
          stopOnHover:true,
          itemsCustom:data.itemscustom,
          autoPlay:data.autoplay,
          transitionStyle:data.transition, 
          beforeInit:background,
          afterAction:animated,
          navigationText:['<i class="fa fa-angle-left" aria-hidden="true"></i>','<i class="fa fa-angle-right" aria-hidden="true"></i>'],
        });
        $(this).find('.owl-controls').css('left',data.control+'px');
      });
    }
    //console.log("FOUN DOUN FOUND")
   if (num_count >= 3  ) {	
  clearInterval(intDeal);
  }
  
     },2000)
  
  
  }
  owl_controller()

}

